<?php   function pr(array $array)   {      echo '<pre>';      print_r($array);      echo '</pre>';   }	// generates textual tables from given data		/*		Example:		table($array);	*/		function table($data)	{		$keys = array_keys(end($data));		$size = array_map('strlen', $keys);				foreach(array_map('array_values', $data) as $e)			$size = array_map('max', $size,				array_map('strlen', $e));		foreach($size as $n) {			$form[] = "%-{$n}s";			$line[] = str_repeat('-', $n);		}				$form = '| ' . implode(' | ', $form) . " |\n";		$line = '+-' . implode('-+-', $line) . "-+\n";		$rows = array(vsprintf($form, $keys));				foreach($data as $e)			$rows[] = vsprintf($form, $e);      echo "<pre>\n";		echo $line . implode($line, $rows) . $line;      echo "</pre>\n";	}?>